INSERT INTO `system_text` (`id`, `lang`, `text`, `author`, `author_edit`, `time_create`, `time_edit`) VALUES ('ceta_title', 'deDE', 'CETA ist das Freihandelsabkommen mit Kanada. Stimme darüber ab, ob der Bundestag CETA zustimmen oder ablehnen soll.', '1', '1', '2017-11-23 23:31:09', '2017-11-23 23:31:09');
