INSERT INTO `system_text` (`id`, `lang`, `text`, `author`, `author_edit`, `time_create`, `time_edit`) VALUES ('euro_title', 'deDE', 'Der Euro ist die verbindliche Währung der soganannten Euro-Zone innerhalb der Europäischen Union. Stimme darüber ab, ob der Bundestag aus dem Euro aussteigen oder verbleiben soll.', '1', '1', '2017-11-23 23:42:30', '2017-11-23 23:42:30');
