INSERT INTO `system_text` (`id`, `lang`, `text`, `author`, `author_edit`, `time_create`, `time_edit`) VALUES ('euro_text', 'deDE', '<p>Seit seiner Einführung hat der Euro viele Vereinfachungen gebracht. Nahezu überall in der EU mit einer Währung zu bezahlen, ist total praktisch. Und doch birgt eine gemeinsame Währung wirtschaftlich unterschiedlich starker Länder währungspolitische Probleme. Und diese Probleme tragen zur Zeit unerkannt eher zur Spaltung Europas als zu dessen Vereinigung bei.</p><p>Wie lange dieser Zustand noch zu wirtschaftlichen Lasten der gesamten südlicheren und fiskalischen Lasten der nördlicheren europäischen Länder aufrechtzuerhalten ist, steht offen. Sicher ist, das dieses Konstruktionsproblem sich eher verschärft als vermindert.</p><h5>Was können wir jetzt tun?</h5><p>DEMOCRACY ist der großangelegte Versuch,&nbsp;unsere Bürgerinteressen deutlich und pragmatisch gegenüber unseren Abgeordneten zu vertreten.&nbsp;Eine&nbsp;gemeinnützige Plattform aufbauen, die&nbsp;alle Bürgerinnen und Bürger, unabhängig von Ort, Zeit und Alter zu diesem Zweck verbindet?</p><p>Technisch gesehen, haben wir dazu alle Möglichkeiten in der Hand.<br>Die App DEMOCRACY ist der Vorschlag, diese zu nutzen.<br>Entscheide DU.</p>', '1', '1', '2017-11-23 23:44:30', '2017-11-23 23:44:30');
INSERT INTO `system_text_tag` (`id`, `tag`) VALUES ('euro_text', 'democracy');
INSERT INTO `system_text` (`id`, `lang`, `text`, `author`, `author_edit`, `time_create`, `time_edit`) VALUES ('gez_title', 'deDE', 'Der Rundfunkbeitrag&amp;nbsp;ist in der&amp;nbsp;Bundesrepublik Deutschland&amp;nbsp;das gegenwärtige Modell zur&amp;nbsp;Finanzierung&amp;nbsp;der&amp;nbsp;öffentlich-rechtlichen Rundfunkanstalten. Stimme darüber ab, wie die Mittel daraus verwendet werden sollen.', '1', '1', '2017-11-23 23:46:16', '2017-11-23 23:46:16');
